import React, { useRef, useEffect, useState } from "react";

import "./App.css";
import "antd/dist/antd.css"; // or 'antd/dist/antd.less'
import { LinkOutlined } from "@ant-design/icons";
import { Empty, Image } from "antd";

import { Input, Space, List } from "antd";
import { AudioOutlined } from "@ant-design/icons";
const { Search } = Input;

// 测试数据
// let data = [
//   { file_key: "FqtC3hxXWgUy4HRZ4At8MC", node_id: "1:22", characters: "策划端请关端注" },
//   {
//     file_key: "FqtC3hxXWgUy4HRZ4At8MC",
//     node_id: "1:23",
//     characters: "请务必在「说明链接」一栏贴上工单地址 或 交互地址",
//   },
//   {
//     file_key: "FqtC3hxXWgUy4HRZ4At8MC",
//     node_id: "1:24",
//     characters: "备注信息建议在「主要备注信息」中记录，否则 CFun 成员将无法查看备注",
//   },
//   { file_key: "FqtC3hxXWgUy4HRZ4At8MC", node_id: "1:29", characters: "平台" },
//   { file_key: "FqtC3hxXWgUy4HRZ4At8MC", node_id: "1:31", characters: "移动端" },
//   { file_key: "FqtC3hxXWgUy4HRZ4At8MC", node_id: "1:33", characters: "PC 端" },
//   { file_key: "FqtC3hxXWgUy4HRZ4At8MC", node_id: "1:35", characters: "Web 端" },
//   { file_key: "FqtC3hxXWgUy4HRZ4At8MC", node_id: "1:38", characters: "修订记录" },
//   { file_key: "FqtC3hxXWgUy4HRZ4At8MC", node_id: "1:40", characters: "登录后点击" },
// ];

class Main extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      info: {},
      keyword: "",
      data: [],
    };
  }

  componentDidMount() {
    fetch("http://fe.dev.cc.163.com/meishu/other/miniTool/designSearch/my_json.json")
      .then((req) => req.json())
      .then((req_json) => {
        this.setState({
          data: req_json["data"],
        });
      });
  }

  onSearch = (value) => {
    if (value == "") {
    } else {
      this.setState({
        keyword: value,
      });
    }
  };

  render() {
    let listItems = [];
    console.log("render:");

    // 存储待查数据
    let my_data = [];
    my_data = this.state.data;

    let characters_Temp = "";
    let pageName_Temp = "";

    // 关键字
    let kw = this.state.keyword;
    kw = kw.toString();
    let my_html = "";
    let link = "";
    console.log(my_data[0]);

    if (kw == "") {
      return (
        <div>
          <div className="SearchBox">
            <div>
              <Search placeholder="搜索活动相关的交互、视觉、动画规范" onSearch={this.onSearch.bind()} enterButton />
            </div>
          </div>

          <ul className="details logoBox"><Image preview={false} width={200} src="http://cc.fp.ps.netease.com/file/622584f7370860322c8c0e32OUC3KxfL04" /></ul>
        </div>
      );
    }

    my_data.forEach((item, index) => {
      if (item.characters == undefined) {
        item.characters = item;
      }



      if (item.characters.indexOf(kw) > -1 || (item.type=='notion' && item.page_name.indexOf(kw) > -1)) {

        console.log(item);

        // 添加样式
        characters_Temp = item.characters.replace(
          new RegExp(kw, "g"),
          "<b style=color:#fff;background:#F66375;>" + kw + "</b>"
        );
        pageName_Temp = item.page_name.replace(
          new RegExp(kw, "g"),
          "<b style=color:#fff;background:#F66375;>" + kw + "</b>"
        );

        if (item.type = "figma") {
          link = "https://www.figma.com/file/" + item.file_key + "/" + "?node-id=" + item.node_id;
        }else if (item.type = "notion") {
          link = "https://www.notion.so/"+ item.node_id;
        }

        my_html = (
          <li>
            <div className="header">
              <span className="file_name">{item.file_name}</span>
              <span> - </span>
              <span className="page_name" dangerouslySetInnerHTML={{ __html: pageName_Temp }}></span>
              <a target="_Blank" href={link}>
                <LinkOutlined />
              </a>
            </div>

            <div className="list_content" key={index} dangerouslySetInnerHTML={{ __html: characters_Temp }}></div>
          </li>
        );
        listItems.push(my_html);
      }
    });

    console.log("listItems.length:");
    console.log(listItems.length);

    let req;

    if (listItems.length > 0) {
      req = <ul className="details">{listItems}</ul>;
    } else {
      req = (
        <ul className="details">
          <Empty image={Empty.PRESENTED_IMAGE_SIMPLE} />
        </ul>
      );
    }

    return (
      <div>
        <div className="SearchBox">
          <div>
            <Search placeholder="搜索活动相关的交互、视觉、动画规范" onSearch={this.onSearch.bind()} enterButton />
          </div>
        </div>

        {req}
      </div>
    );
  }
}

function App() {
  return (
    <div className="App">
      <Main />
    </div>
  );
}

export default App;
