import React, { useRef, useEffect, useState } from "react";

import "./App.css";
import "antd/dist/antd.css"; // or 'antd/dist/antd.less'

import { Input, Space } from "antd";
import { AudioOutlined } from "@ant-design/icons";
const { Search } = Input;

let data = [
  { file_key: "FqtC3hxXWgUy4HRZ4At8MC", node_id: "1:22", characters: "策划端请关端注" },
  {
    file_key: "FqtC3hxXWgUy4HRZ4At8MC",
    node_id: "1:23",
    characters: "请务必在「说明链接」一栏贴上工单地址 或 交互地址",
  },
  {
    file_key: "FqtC3hxXWgUy4HRZ4At8MC",
    node_id: "1:24",
    characters: "备注信息建议在「主要备注信息」中记录，否则 CFun 成员将无法查看备注",
  },
  { file_key: "FqtC3hxXWgUy4HRZ4At8MC", node_id: "1:29", characters: "平台" },
  { file_key: "FqtC3hxXWgUy4HRZ4At8MC", node_id: "1:31", characters: "移动端" },
  { file_key: "FqtC3hxXWgUy4HRZ4At8MC", node_id: "1:33", characters: "PC 端" },
  { file_key: "FqtC3hxXWgUy4HRZ4At8MC", node_id: "1:35", characters: "Web 端" },
  { file_key: "FqtC3hxXWgUy4HRZ4At8MC", node_id: "1:38", characters: "修订记录" },
  { file_key: "FqtC3hxXWgUy4HRZ4At8MC", node_id: "1:40", characters: "登录后点击" },
];

class Main extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      info: {},
      keyword: "1",
    };
  }

  onSearch = (value) => {
    if (value == "") {
    } else {
      this.setState({
        keyword: value,
      });
    }
  };

  render() {
    let listItems = [];

    fetch('http://fe.dev.cc.163.com/meishu/other/miniTool/helloUser/config.json').then((req)=>req.json()).then((req_json)=>{
      console.log(req_json);
    })

    // listItems.forEach((item, index) => {
    //   // 清空之前的样式
    //   console.log("listItems.forEach:");
    //   console.log(item);
    //   if (item.props.dangerouslySetInnerHTML.__html.indexOf("<b style=color:red;>") > -1) {
    //     item.props.dangerouslySetInnerHTML.__html = item.props.dangerouslySetInnerHTML.__html.replace(
    //       "<b style=color:red;>",
    //       ""
    //     );
    //     item.props.dangerouslySetInnerHTML.__html = item.props.dangerouslySetInnerHTML.__html.replace("</b>", "");
    //   }
    // });

    let strTemp = ''
    data.forEach((item, index) => {
      let kw = this.state.keyword;
      kw = kw.toString();
      if (item.characters.indexOf(kw) > -1) {
        console.log("list:");
        console.log(item);

        // 添加样式
        strTemp = item.characters.replace(new RegExp(kw, "g"), "<b style=color:red;>" + kw + "</b>");
        console.log(strTemp);
        listItems.push(<li key={index} dangerouslySetInnerHTML={{ __html: strTemp }}></li>);
      }
    });

    console.log(listItems);

    return (
      <div>
        <Search placeholder="input search text" onSearch={this.onSearch.bind()} enterButton />

        <ul className="details">{listItems}</ul>
      </div>
    );
  }
}

function App() {
  return (
    <div className="App">
      <Main />
    </div>
  );
}

export default App;
